do local function run(msg, matches) if matches[1] == 'me' or 'من کیم' then if is_sudo(msg) then send_document(get_receiver(msg),'./sticker/'..msg.from.id..'.webp' or './sticker/sudo.webp', ok_cb, false) return "بابایی منی😍💝" elseif is_admin1(msg) then send_document(get_receiver(msg),'./sticker/'..msg.from.id..'.webp' or './sticker/admin.webp', ok_cb, false) return "مامان من دیگ😍💕" elseif is_owner(msg) then send_document(get_receiver(msg),'./sticker/'..msg.from.id..'.webp' or './sticker/owner.webp', ok_cb, false) return "مدیر گروهی" elseif is_momod(msg) then send_document(get_receiver(msg),'./sticker/'..msg.from.id..'.webp' or './sticker/mod.webp', ok_cb, false) return "ادمینی دیگ خنگ😊" else send_document(get_receiver(msg),'./sticker/'..msg.from.id..'.webp' or './sticker/member.webp', ok_cb, false) return "هیچی نیستی😐🤣" end endend return { patterns = { "^[!/#]([Mm]e)$", "^([Mm]e)$", "^من کیم$" }, run = run}end